import React from "react";
import { validateEmail } from "../../validate";
import {Button} from "@mui/material";
import { Formik } from "formik";
import Input from './../../components/Input'

const MyForm = (props)=>{

  const items = (values,errors,touched,handleChange,handleBlur,isSubmitting)=>(props.formItem.map(item=>{

    switch(item.element){
      case 'Input':{
        const {type,name,placeholder,sx} = item.props
        return(
          <Input
              type={type}
              name={name}
              onChange={handleChange}
              onBlur={handleBlur}
              value={values[name]}
              errors={errors[name]}
              touched={touched[name]}
              placeholder={placeholder}
           />
        )
      }

      case 'Button':{
        const {type,sx} = item.props
        return(
          <Button type="submit" disabled={isSubmitting} sx={{marginTop:'10px'}} >
            Submit
          </Button>
        )
      }

      default: return null
    }
  }))

  return (
    <Formik
          initialValues={props.initialValues}
          validate={validateEmail}
          onSubmit={props.onSubmit}
        >
        {({values,errors,
          touched,handleChange,
          handleBlur,handleSubmit,isSubmitting}) => (
          <form 
          onSubmit={handleSubmit}
          onKeyPress={e=>{if(e.key === 'Enter')handleSubmit()}}
          >
            {item(values,errors,touched,handleChange,handleBlur,isSubmitting)}
          </form>
        )}
        </Formik>

  )
}

export default MyForm
