import React from "react";
import { validateEmail } from "../../validate";
import {Button, TextField} from "@mui/material";
import { Formik } from "formik";
import Input from './../../components/Input'
import { useStyles } from "./style";

const MyForm = (props)=>{

  const s = useStyles()

  const items = (values,errors,touched,handleChange,handleBlur,isSubmitting)=>(props.formItem.map((item,index)=>{

    switch(item.element){
      case 'Input':{
        const {type,name,placeholder,sx} = item.propsItem
        return(
          <Input
              type={type}
              name={name}
              onChange={handleChange}
              onBlur={handleBlur}
              value={values[name]}
              errors={errors[name]}
              touched={touched[name]}
              placeholder={placeholder}
              sx={sx}
              key={index}
           />
        )
      }

      default: return null
    }
  }))

  const textButton = ()=>{
    switch(props.typeForm){
      case 'singin': return 'Sing In'
      case 'singup': return 'Sing Up'
      case 'changeProfile': return 'Save'

      default : return null
    }
  }

  return (
    <Formik
          initialValues={props.initialValues}
          validate={validateEmail}
          onSubmit={props.onSubmit}
        >
        {({values,errors,touched,handleChange,handleBlur,handleSubmit,isSubmitting}) => (
          <form 
          onSubmit={handleSubmit}
          >
            {items(values,errors,touched,handleChange,handleBlur,isSubmitting)}
            <div className={s.blockSubmit}>
              {props.typeForm=='singup'?<div className={s.staySing}>Stay singed in?</div>:null}
              <div className={s.blockButton}>
                <Button type='submit' disabled={isSubmitting} variant='contained'>
                  {textButton()}
                </Button>
              </div>
            </div>
            <div className={s.createProfile}><TextField>Create an account</TextField></div>
          </form>
        )}
        </Formik>

  )
}

export default MyForm
