import "./App.css";
import Main from "./pages/main";
import { Route, Routes } from "react-router-dom";
import Login from "./pages/login";
import SignUp from "./pages/signup";
import Profile from "./pages/profile";
import Update from "./pages/update";
import Product from "./pages/product";
import Header from "./section/header";
import { Navigate, useLocation, useRoutes } from "react-router";
import Loading from "./pages/loading";
import { useSelector } from "react-redux";
import ErrorApi from "./pages/error";
import PrivateRoute from "./routes/privateRoute";

function App() {
  const token = localStorage.getItem("token");
  const location = useLocation();
  const isLoading = useSelector((state) => state.statusReducer.isLoading);
  const isError = useSelector((state) => state.statusReducer.isError);
  const routes = useRoutes([
    {
      path: "/login",
      element: (<Login/>),
    },
    {
      path: "/signup",
      element: (<SignUp/>),
    },
    {
      path: "/",
      element: (
        <PrivateRoute>
          <Main />}
        </PrivateRoute>
      ),
    },
    {
      path: "/profile",
      element: (
        <PrivateRoute>
          <Profile />
        </PrivateRoute>
      ),
    },
    {
      path: "/profile/update",
      element: (
        <PrivateRoute>
          <Update />
        </PrivateRoute>
      ),
    },
    {
      path: "/product",
      element: (
        <PrivateRoute>
          <Product />
        </PrivateRoute>
      ),
    },
  ]);

  if (isLoading) {
    return (
      <>
        <Header />
        <Loading />
      </>
    );
  }

  if (isError) {
    return (
      <>
        <Header />
        <ErrorApi error={isError} />
      </>
    );
  }



  return (
    <>
      <Header />
      {/* <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<SignUp />} />
      </Routes>
        <PrivateRoute exact path="/" element={<Main />} />
        <PrivateRoute path="/profile" element={<Profile />} />
        <PrivateRoute path="/profile/update" element={<Update />} />
        <PrivateRoute path="/product" element={<Product />} /> */}
        routes
    </>
  );
}

export default App;
