import { takeEvery } from "@redux-saga/core/effects";
import { productApi } from "../../../../API";


async function getProdWithId(id){
  return await productApi.getProductWithId(id)
}

function* workerSaga({id}){
  const data = yield getProdWithId(id)
}

export default function* getProductWithIdSaga(){
  yield takeEvery('GET_PROD_WITH_ID',workerSaga)
}
