import {takeEvery,call} from 'redux-saga/effects'
import {userApi} from './../../../API'


async function logoutUser(token){
  return await userApi.logout(token)
}

function* workerSaga({token}){
  const data = logoutUser(token) 
  if(data.status === 200){
    yield put({type:'SET_DATA_USER',data:{token:data.data.user.accessToken,firstName:data.data.user.firstName,lastName:data.data.user.lastName}})
  }
}

export default function* logoutSaga(){
  yield takeEvery('LOGOUT',workerSaga)
}
