import { takeEvery } from "@redux-saga/core/effects";
import { productApi } from "../../../../API";


async function updateProd(name,manufacture,calories,rating,description,category,amount,id){
  return await productApi.updateProduct(name,manufacture,calories,rating,description,category,amount,id)
}

function* workerSaga({name,manufacture,calories,rating,description,category,amount,id}){
  const data = yield updateProd(name,manufacture,calories,rating,description,category,amount,id)
}

export default function* updateProductSaga(){
  yield takeEvery('UPDATE_PRODUCT',workerSaga)
}
