import { spawn,call,all } from "@redux-saga/core/effects"
import signUpSaga from "./users/signup"
import loginSaga from './users/login'
import renewToken from './users/renewToken'
import logoutSaga from "./users/logout"
import getUserSaga from "./users/getme"
import updateUserSaga from "./users/userUpdate"
import deleteUserSaga from "./users/deleteUser"


export default function* rootSaga (){

  const sagas = [signUpSaga,loginSaga,renewToken,logoutSaga,getUserSaga,updateUserSaga,deleteUserSaga,
  ]


  const retrySagas = yield sagas.map(saga=>{
    return spawn(function* (){
      while(true){
        try{
          yield call(saga)
        }
        catch(e){
          console.log(e)
        }
      }
    })
  })

  yield all(retrySagas)
}
