import { put, takeEvery } from "@redux-saga/core/effects";
import { productApi } from "../../../../API";

async function createProd(name,manufacture,calories,rating,description,category,amount){
  return await productApi.createProduct(name,manufacture,calories,rating,description,category,amount)
}

function* workerSaga({name,manufacture,calories,rating,description,category,amount}){
  const data = yield createProd(name,manufacture,calories,rating,description,category,amount)
}

export default function* createProductSaga(){
  yield  takeEvery('CREATE_PRODUCT',workerSaga)
}
