import { takeEvery ,put} from "@redux-saga/core/effects";
import { productApi } from "../../../../API";


async function getProds(token){
  return await productApi.getProducts(token)
}

function* workerSaga({token}){
  const data = yield getProds(token)
  if(data.status === 200){
    yield 
  }
}

export default function* getProductsSaga(){
  yield takeEvery('GET_PRODUCTS',workerSaga)
}
