import { spawn,call,all } from "@redux-saga/core/effects"
import createProductSaga from "./products/createProduct"
import deleteProductSaga from "./products/deleteProducts"
import getProductsSaga from "./products/getProducts"
import getProductWithIdSaga from "./products/getProductWithId"
import updateProductSaga from "./products/updateProduct"
import userSaga from "./user"



export default function* rootSaga (){

  const sagas = [userSaga,createProductSaga,deleteProductSaga,getProductsSaga,getProductWithIdSaga,updateProductSaga]


  const retrySagas = yield sagas.map(saga=>{
    return spawn(function* (){
      while(true){
        try{
          yield call(saga)
        }
        catch(e){
          console.log(e)
        }
      }
    })
  })

  yield all(retrySagas)
}
