import * as axios from 'axios'

const instanse = axios.create({
  baseURL:'https://mock-api-nodejs.herokuapp.com'
})


export const userApi = {
  signup:(firstName,lastName,email,password)=>instanse.post('/users/signup',{firstName,lastName,email,password}).then(respose=>respose.data),
  login:(email,password)=>instanse.post('/users/login',{email, password}),
  renewToken:(token)=>instanse.post('/users/renewAcessToken',{token}),
  logout:(token)=>instanse.patch('/users/logout',{headers:{ Authorization: `Bearer ${token}` }}),
  getMe:()=>instanse.get('/users/me'),
  userUpdate:(firstName,lastName,email)=>instanse.patch('/users/update',{firstName,lastName,email})
}
