import React from "react";
import { validateEmail } from "../../validate";
import { Button } from "@mui/material";
import { Formik,useFormik } from "formik";
import Input from "../Input";
import { useStyles } from "./style";
import { useNavigate } from "react-router";

const MyForm = (props) => {
  const s = useStyles();
  const navigate = useNavigate();
  const formik = useFormik({initialValues:props.initialValues},props.onSubmit)

  const items = (handleBlur) =>
    props.formItem.map((item, index) => {
      switch (item.element) {
        case "Input": {
          const { type, name, placeholder, sx } = item.propsItem;
          return (
            <Input
              type={type}
              name={name}
              onChange={formik.handleChange}
              onBlur={handleBlur}
              value={formik.values?formik.values[name]:''}
              errors={formik.errors?formik.errors[name]:''}
              touched={formik.touched?formik.touched[name]:''}
              placeholder={placeholder}
              sx={sx}
              key={index}
            />
          );
        }

        default:
          return null;
      }
    });

  const btnText = {
    signin: "Sign In",
    signup: "Sign Up",
    changeProfile: "Save"
  };

  return (
    <Formik
     onSubmit={formik.handleSubmit}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
      }) => (
        <form onSubmit={handleSubmit}>
          {items(
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            isSubmitting
          )}
          <div className={s.blockSubmit}>
            {props.typeForm == "signin" &&
              <div className={s.staySing}>
                <input
                  type="checkbox"
                  name="checkbox"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.checkbox}
                  errors={errors.checkbox}
                  touched={touched.checkbox}
                />
                Stay signed in?
              </div>
            }
            <div className={s.blockButton}>
              <Button type="submit" disabled={isSubmitting} variant="contained">
                {btnText[props.typeForm]}
              </Button>
            </div>
          </div>
          {props.typeForm == "signin" &&
            <div className={s.createProfile}>
              <Button
                onClick={() => {
                  navigate("/signup");
                }}
              >
                Create an account
              </Button>
            </div>
          }
        </form>
      )}
    </Formik>
  );
};

export default MyForm;
