import { takeEvery } from "@redux-saga/core/effects";
import { productApi } from "../../../../API";

async function deleteProd(id,token){
  return await productApi.deleteProducts(id,token)
}

function* workerSaga({id,token}){
  const data = yield deleteProd(id,token)
}

export default function* deleteProductSaga(){
  yield takeEvery('DELETE_PRODUCT',workerSaga)
}
