import {takeEvery,call} from 'redux-saga/effects'
import {userApi} from './../../../API'


async function userUpdate(firstName,lastName,email){
  const data = await userApi.userUpdate(firstName,lastName,email)
}

function* workerSaga({firstName,lastName,email}){
 yield call(userUpdate(firstName,lastName,email)) 
}

export default function* userUpdateSaga(){
  yield takeEvery('USERUPDATE',workerSaga)
}
