import instanse from './index';
export const userApi = {
  signup: payload => instanse.post('/users/signup', { ...payload
  }).then(respose => respose),
  login: payload => instanse.post('/users/login', { ...payload
  }),
  renewToken: payload => instanse.post('/users/renewAcessToken', { ...payload
  }),
  logout: () => instanse.patch('/users/logout').then(respose => respose),
  getMe: () => instanse.get('/users/me'),
  userUpdate: (firstName, lastName, email, token) => instanse.patch('/users/update', {
    firstName,
    lastName,
    email
  }, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  }).then(respose => respose),
  userDelete: payload => instanse.delete('/users/delete', {
    headers: {
      Authorization: `Bearer ${payload.token}`
    }
  }).then(respose => respose)
};
