import {takeEvery,call,put} from 'redux-saga/effects'
import {userApi} from './../../../API'


async function loginUser(email,password){
  const data = await userApi.login(email,password)
  if(data.status === 200){
    put({type:'SET_DATA_USER',data:{token:data.data.user.accessToken,firstName:data.data.user.firstName,lastName:data.data.user.lastName}})
  }
}

function* workerSaga({email,password}){
  yield call(loginUser(email,password))
}

export default function* loginSaga(){
  yield takeEvery('LOGIN',workerSaga)
}
