import instanse from "./index"


  export const signup=(payload)=>instanse.post('/users/signup',{...payload}).then(respose=>respose)

  export const login=(payload)=>instanse.post('/users/login',{...payload})

  export const renewToken=(payload)=>instanse.post('/users/renewAcessToken',{...payload})

  export const logout=(payload)=>instanse.patch('/users/logout',null,{headers:{Authorization: `Bearer ${payload.token}`}}).then(respose=>respose)

  export const getMe=()=>instanse.get('/users/me')

  export const userUpdate=(firstName,lastName,email,token)=>instanse.patch('/users/update',{firstName,lastName,email},{headers:{Authorization: `Bearer ${token}`}}).then(respose=>respose)

  export const userDelete=(payload)=>instanse.delete('/users/delete',{headers:{Authorization: `Bearer ${payload.token}`}}).then(respose=>respose)
