const initialState = {
  products:[]
}


const productReducer = (state = initialState,action)=>{
  switch(action.type){

    case 'SET_PRODUCTS':{
      return{
        ...state,
        ...action.data
      }
    }

    default: return state
  }
}


export const createProduct = (name,manufacture,calories,rating,description,category,amount,token)=>{
  return{type:'CREATE_PRODUCT',name,manufacture,calories,rating,description,category,amount,token}
}

export const deleteProduct = (id,token)=>{
  return{type:'DELETE_PRODUCT',id,token}
}

export const getProducts = (token)=>{
  return{type:'GET_PRODUCTS',token}
}

export const getProdWithId = (id,token)=>{
  return{type:'GET_PROD_WITH_ID',id,token}
}

export const updateProduct = (name,manufacture,calories,rating,description,category,amount,id,token)=>{
  return{type:'UPDATE_PRODUCT',name,manufacture,calories,rating,description,category,amount,id,token}
}
export default productReducer
