import { takeLatest,put,call,all,takeEvery } from "@redux-saga/core/effects"
import { DELETEUSER, GETUSER, LOGIN, LOGOUT, RENEWTOKEN, SETDATAUSER, SIGNUP, USERUPDATE } from "../actionTypes/user"
import { userApi } from "../services/user"


/* Worker */

function* userDelete(action){
  const {token} = action.payload
  const data = yield call(userApi.userDelete,{token})
  if(data.status === 200){
    yield put({type:SETDATAUSER,data:{token:'',firstName:'',lastName:'',email:''}})
  }
}

function* getUser(){
  const data = yield call(userApi.getMe())
}


function* loginUser(action){
  try{
    console.log(action)
      const {email,password} = action.payload
      const data = yield call(userApi.login,{email,password})
      if(data.status === 200){
        yield put({type:SETDATAUSER,data:{token:data.data.user.accessToken,firstName:data.data.user.firstName,lastName:data.data.user.lastName,email:data.data.user.email}})
      }
  }
  catch(error){
    console.log(error)
  }

}


function* logoutUser(action){
  const {token} = action.payload
  const data = yield call(userApi.logout,{token})
  if(data.status === 200){
    yield put({type:SETDATAUSER,data:{token:'',firstName:'',lastName:''}})
  }
}

function* renewToken(action){
  const {token} = action.payload
  const data = yield call(userApi.renewToken,{token})
}


function* singUpUser(action){
  const {firstName,lastName,email,password} = action.payload
  const data = yield call(userApi.signup,{firstName,lastName,email,password})
  if(data.status === 201){
    yield put({type:SETDATAUSER,data:{token:data.data.user.accessToken,firstName:data.data.user.firstName,lastName:data.data.user.lastName,email:data.data.user.email}})
  }
} 


function* userUpdate(action){
  const {firstName,lastName,email,token} =action.payload
  const data = yield call(userApi.userUpdate,{firstName,lastName,email,token})
  if(data.status === 200){
    yield put({type:SETDATAUSER,data:{firstName:data.data.updatedUser.firstName,lastName:data.data.updatedUser.lastName,email:data.data.updatedUser.email}})
  }
}



/* Watchers */

function* watchDeleteUserSaga(){
  yield takeLatest(DELETEUSER,userDelete)
}

function* watchGetUserSaga(){
  yield takeLatest(GETUSER,getUser)
}

function* watchUpdateUserSaga(){
  yield takeLatest(USERUPDATE,userUpdate)
}

function* watchSignUpSaga(){
  yield takeLatest(SIGNUP,singUpUser)
}
function* watchRenewTokenSaga(){
  yield takeLatest(RENEWTOKEN,renewToken)
}

function* watchLogoutSaga(){
  yield takeLatest(LOGOUT,logoutUser)
}

function* watchLoginSaga(){

  yield takeLatest(LOGIN,loginUser)
}

export default function* userSaga(){
  yield all([
    watchDeleteUserSaga(),
    watchGetUserSaga(),
    watchUpdateUserSaga(),
    watchSignUpSaga(),
    watchRenewTokenSaga(),
    watchLogoutSaga(),
    watchLoginSaga()
  ])
}
