import { takeEvery } from "@redux-saga/core/effects";
import { productApi } from "../../../../API";


async function updateProd(name,manufacturer,calories,rating,description,category,amount,id,token){
  return await productApi.updateProduct(name,manufacturer,calories,rating,description,category,amount,id,token)
}

function* workerSaga({name,manufacturer,calories,rating,description,category,amount,id,token}){
  const data = yield updateProd(name,manufacturer,calories,rating,description,category,amount,id,token)
}

export default function* updateProductSaga(){
  yield takeEvery('UPDATE_PRODUCT',workerSaga)
}
