const initialState = {
  token:'',
  firstName:'',
  lastName:'',
  email:''
}

const authReducer = (state = initialState,action)=>{
  switch(action.type){

    case 'SET_DATA_USER':{
      return{
        ...state,
        ...action.data
      }
    }
    default: return state
  }
}

export default authReducer
