import {Button} from "@mui/material";
import { Formik } from "formik";
import React from "react";
import Header from "../../section/header";
import { useStyles } from "./style";
import Input from './../../components/Input'
import { connect, useDispatch } from "react-redux";
import { userSignUp } from "../../store/actions/user";
import { useNavigate } from "react-router";
import { validateEmail } from "../../validate";
import MyForm from "../../section/form";



const SignUp = (props)=>{

  const s = useStyles()
  const navigate = useNavigate()
  const dispatch = useDispatch()




  const onSubmit = (values, { setSubmitting }) => {
    const {firstName,lastName,email,password} = values
    dispatch(userSignUp({firstName,lastName,email,password})) 
    setSubmitting(false)
    navigate('/')
}

const formItem = [
  {
    element:'Input',
    propsItem:{
      type:"firstName",
      name:"firstName",
      placeholder:'First name'
    }

  },
  {
    element:'Input',
    propsItem:{
      type:"lastName",
      name:"lastName",
      placeholder:'Last name',
      sx:{marginTop:'20px'}
    }

  },
  {
    element:'Input',
    propsItem:{
      type:"email",
      name:"email",
      placeholder:'Email',
      sx:{marginTop:'20px'}
    }

  },
  {
    element:'Input',
    propsItem:{
      type:"password",
      name:"password",
      placeholder:'Password',
      sx:{marginTop:'20px'}
    }

  },
  {
    element:'Button',
    propsItem:{
      type:"submit",
      sx:{marginTop:'10px'}
    }

  }
]


  return(
    <div>
      <Header/>
      <div className={s.signUpForm}>
        <MyForm onSubmit={onSubmit} initialValues={{firstName,lastName,email}} formItem={formItem}/>
        <Formik
          initialValues={{firstName:'',lastName:'',email: '', password: '' }}
          validate={validateEmail}
          onSubmit={onSubmit}
        >
        {({values,errors,
          touched,handleChange,
          handleBlur,handleSubmit,isSubmitting}) => (
          <form 
          onSubmit={handleSubmit}
          onKeyPress={e=>{if(e.key === 'Enter')handleSubmit()}}
          >
            <Input
              type="firstName"
              name="firstName"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.firstName}
              errors={errors.firstName}
              touched={touched.firstName}
              placeholder='First name'
            />
            <Input
              type="lastName"
              name="lastName"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.lastName}
              errors={errors.lastName}
              touched={touched.lastName}
              placeholder='Last name'
              sx={{marginTop:'20px'}}
              />
            <Input
              type="email"
              name="email"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
              errors={errors.email}
              touched={touched.email}
              placeholder='Email'
              sx={{marginTop:'20px'}}
            />
            <Input
              type="password"
              name="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
              errors={errors.password}
              touched={touched.password}
              placeholder='Password'
              sx={{marginTop:'20px'}}
            />
            <Button type="submit" disabled={isSubmitting} sx={{marginTop:'10px'}} >
              Submit
            </Button>
          </form>
        )}
        </Formik>
      </div>
    </div>
  )
}

export default SignUp
