import React, { useEffect } from "react";
import { connect, useSelector } from "react-redux";
import { useNavigate } from "react-router";
import { compose } from "redux";
import withRedirectLogin from "../../hoc/withRedirectLogin";
import { useStyles } from "./style";
import { userUpdate } from "../../store/actions/user";
import {Button} from "@mui/material";
import { Formik } from "formik";
import Header from "../../section/header";
import Input from './../../components/Input'
import { validateEmail } from "../../validate";
import { useDispatch } from "react-redux";



const Update = (props)=>{

  const s = useStyles()
  const navigate = useNavigate()
  const {firstName,lastName,email,token} = useSelector(state=>state.authReducer)
  const dispatch = useDispatch()

  useEffect(()=>{
    if(token === '')navigate('/login')
  },[])

  const onSubmit = (values, { setSubmitting }) => {
    const {firstName,lastName,email} = values
    dispatch(userUpdate({firstName,lastName,email}))
    setSubmitting(false)
    navigate('/profile')
  }

  return(
    <div>
      <Header/>
      <div className={s.updateForm}>
        <Formik
          initialValues={{firstName,lastName,email}}
          validate={validateEmail}
          onSubmit={onSubmit}
        >
        {({values,errors,
          touched,handleChange,
          handleBlur,handleSubmit,isSubmitting}) => (
          <form 
          onSubmit={handleSubmit}
          onKeyPress={e=>{if(e.key === 'Enter')handleSubmit()}}
          >
            <Input
              type="firstName"
              name="firstName"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.firstName}
              errors={errors.firstName}
              touched={touched.firstName}
              placeholder='First name'
            />
            <Input
              type="lastName"
              name="lastName"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.lastName}
              errors={errors.lastName}
              touched={touched.lastName}
              placeholder='Last name'
              sx={{marginTop:'20px'}}
              />
            <Input
              type="email"
              name="email"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
              errors={errors.email}
              touched={touched.email}
              placeholder='Email'
              sx={{marginTop:'20px'}}
            />
            <Button type="submit" disabled={isSubmitting} sx={{marginTop:'10px'}} >
              Submit
            </Button>
          </form>
        )}
        </Formik>
      </div>
    </div>
  )
} 


export default Update
