import createSagaMiddleware from "@redux-saga/core";
import { applyMiddleware, createStore, compose } from "redux";
import rootSaga from "./sagas";
import { rootReducers } from "./reducers";


const sagaMiddleWare = createSagaMiddleware();
const rdTools = window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__();
const middlewareEnhancer = applyMiddleware(sagaMiddleWare, rdTools);
const composedEnhancers = compose(middlewareEnhancer, monitorReducerEnhancer)

const store = createStore(rootReducers, middlewareEnhancer)

sagaMiddleWare.run(rootSaga)

export default store
