import { LOGIN,SIGNUP,RENEWTOKEN,LOGOUT,GETUSER,DELETEUSER,USERUPDATE} from "../actionTypes/user"



export const user = {
  userLogin:(payload)=>{
    const {email,password} = payload
    return {type:LOGIN,email,password}
  },

  userSignUp:(payload)=>{
    const {firstName,lastName,email,password} = payload
    return {type:SIGNUP,firstName,lastName,email,password}
  },

  renewToken:(payload)=>{
    const {token} = payload
    return {type:RENEWTOKEN,token}
  },

  userLogout:(payload)=>{
    const {token} = payload
    return {type:LOGOUT,token}
  },

  getUser:(payload)=>{
    const {token} = payload
    return {type:GETUSER,token}
  },

  deleteUser:(payload)=>{
    const {token} = payload
    return {type:DELETEUSER,token}
  },

  userUpdate:(payload)=>{
    const {firstName,lastName,email,token} = payload
    return {type:USERUPDATE,firstName,lastName,email,token}
  },
}
