const initialState = {
  products:[]
}


const productReducer = (state = initialState,action)=>{
  switch(action.type){

    case 'SET_PRODUCTS':{
      return{
        ...state,
        ...action.data
      }
    }

    default: return state
  }
}


export const createProduct = (name,manufacture,calories,rating,description,category,amount)=>{
  return{type:'CREATE_PRODUCT',name,manufacture,calories,rating,description,category,amount}
}

export const deleteProduct = (id)=>{
  return{type:'DELETE_PRODUCT',id}
}

export const getProducts = ()=>{
  return{type:'GET_PRODUCTS'}
}

export const getProdWithId = (id)=>{
  return{type:'GET_PROD_WITH_ID',id}
}

export const updateProduct = (name,manufacture,calories,rating,description,category,amount,id)=>{
  return{type:'UPDATE_PRODUCT',name,manufacture,calories,rating,description,category,amount,id}
}
export default productReducer
