const initialState = {
  token:'',
  firstName:'',
  lastName:''
}

const authReducer = (state = initialState,action)=>{
  switch(action.type){

    case 'SET_DATA_USER':{
      return{
        ...state,
        ...action.data
      }
    }
    default: return state
  }
}


export const userLogin = (email,password)=>{
  return {type:"LOGIN",email,password}
}

export const userSignUp = (firstName,lastName,email,password)=>{
  return {type:"SIGNUP",firstName,lastName,email,password}
}

export const renewToken = (token)=>{
  return {type:"RENEWTOKEN",token}
}

export const userLogout = ()=>{
  return {type:"LOGIOUT"}
}

export const getUser = ()=>{
  return {type:"GETUSER"}
}

export const userUpdate = (firstName,lastName,email)=>{
  return {type:"USERUPDATE",firstName,lastName,email}
}


export default authReducer
